import React, { Component } from 'react';
import './App.css';
import {CardList} from './CardList.jsx/CardList';
import {SearchField} from './SearchField/SearchField';

class App extends Component {
  constructor() {
    super();
    this.state = { 
        users:[],
        searchfield:'',
    } 
}

componentDidMount() {
fetch('https://jsonplaceholder.typicode.com/users')
.then(response => response.json())
.then(jsonfile => this.setState({users:jsonfile}))
};

// handleChange = (e) => {
//   this.setState({searchfield: e.target.value})
// }

  render() {
    const {users,searchfield} = this.state;
    const usersFiltered = users.filter(user =>
      user.name.toLowerCase().includes(searchfield.value.toLowerCase())
    );

  return (
    <div className="App">
      <SearchField onChange={(e) => {this.setState({searchfield: e.target.value})}} />

      <CardList users={usersFiltered}/>

    </div>
  );
}
}

export default App;
